import React from "react";

class GoogleAuth extends React.Component {
  state = { isSingedIn: null };

  componentDidMount() {
    // window.gapi.load("clent:auth2", () => {
    //   window.gapi.auth2.init({
    //     clientId:
    //       "224585224633-jq789bskv66eh0g1edafs770c7fufn5i.apps.googleusercontent.com",
    //     scope: "email",
    //     plugin_name: "streamy",
    //   });
    // });

    window.gapi.load("clent:auth2", function () {
      window.gapi.auth2
        .init({
          clientId:
            "224585224633-jq789bskv66eh0g1edafs770c7fufn5i.apps.googleusercontent.com",
          scope: "email",
          plugin_name: "streamy",
        })
        .then(() => {
          this.auth = window.gapi.auth2.getAuthInstance();
          this.setState({ isSingedIn: this.auth.isSingedIn.get() });
        });
    });
  }
  renderAuthHelper() {
    if (this.state.isSingedIn === null) {
      return <div>login or not defined</div>;
    } else if (this.state.isSingedIn) {
      return <div>user LogIN</div>;
    } else {
      return <div> user Logout </div>;
    }
  }
  render() {
    return <div>{this.renderAuthHelper()}</div>;
  }
}

export default GoogleAuth;
